import React, { useState } from 'react';
import SelectTeam from './components/SelectTeam';

function App() {
    const [team, setTeam] = useState('');
    const [players, setPlayers] = useState([]);
    const [gameStarted, setGameStarted] = useState(false);

    const handleStartGame = () => {
        setGameStarted(true);
    };

    return (
        <div style={{ padding: 32 }}>
            <h1>NBA Team & Player Selector</h1>
            {!gameStarted ? (
                <>
                    <SelectTeam
                        onSelectTeam={setTeam}
                        onSelectPlayers={setPlayers}
                    />
                    <div style={{ marginTop: 24 }}>
                        <strong>Selected Team:</strong> {team || 'None'}
                        <br />
                        <strong>Selected Players:</strong>
                        <ul>
                            {players.map((p) => (
                                <li key={p}>{p}</li>
                            ))}
                        </ul>
                        <button
                            onClick={handleStartGame}
                            disabled={players.length !== 5}
                        >
                            Start Game
                        </button>
                    </div>
                </>
            ) : (
                <div>
                    <h2>Game Started!</h2>
                    <p>Team: {team}</p>
                    <p>Lineup:</p>
                    <ul>
                        {players.map((p) => (
                            <li key={p}>{p}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
}

export default App;
