import React, { useState, useRef, useEffect } from 'react';
import SelectTeam from './components/SelectTeam';

const QUARTERS = 4;
const MINUTES_PER_QUARTER = 12;
const SECONDS_PER_QUARTER = MINUTES_PER_QUARTER * 60;

function formatTime(seconds) {
  const m = Math.floor(seconds / 60);
  const s = seconds % 60;
  return `${m}:${s.toString().padStart(2, '0')}`;
}

function App() {
  const [teamA, setTeamA] = useState('');
  const [playersA, setPlayersA] = useState([]);
  const [teamB, setTeamB] = useState('');
  const [playersB, setPlayersB] = useState([]);
  const [gameStarted, setGameStarted] = useState(false);

  // Game simulation state
  const [quarter, setQuarter] = useState(1);
  const [clock, setClock] = useState(SECONDS_PER_QUARTER);
  const [scoreA, setScoreA] = useState(0);
  const [scoreB, setScoreB] = useState(0);
  const [gameOver, setGameOver] = useState(false);

  const intervalRef = useRef(null);

  // Start game and simulation
  const handleStartGame = () => {
    setGameStarted(true);
    setQuarter(1);
    setClock(SECONDS_PER_QUARTER);
    setScoreA(0);
    setScoreB(0);
    setGameOver(false);
  };

  // Automatic simulation effect
  useEffect(() => {
    if (gameStarted && !gameOver) {
      intervalRef.current = setInterval(() => {
        handleSimulatePlay();
      }, 500); // 0.5 seconds per play
    } else {
      clearInterval(intervalRef.current);
    }
    return () => clearInterval(intervalRef.current);
    // eslint-disable-next-line
  }, [gameStarted, gameOver, quarter, clock]);

  // Simulate a play: advance clock, randomly assign points
  const handleSimulatePlay = () => {
    if (gameOver) return;

    // Simulate time elapsed for a play (random 5-24 seconds)
    const elapsed = Math.min(clock, Math.floor(Math.random() * 20) + 5);
    let newClock = clock - elapsed;

    // Simulate scoring: 0, 2, or 3 points for a random team
    const scoringTeam = Math.random() < 0.5 ? 'A' : 'B';
    const points = Math.random() < 0.7 ? 2 : 3; // 70% chance for 2, else 3
    if (Math.random() < 0.5) { // 50% chance of scoring
      if (scoringTeam === 'A') setScoreA(prev => prev + points);
      else setScoreB(prev => prev + points);
    }

    // If quarter ends
    if (newClock <= 0) {
      if (quarter < QUARTERS) {
        setQuarter(q => q + 1);
        setClock(SECONDS_PER_QUARTER);
      } else {
        setClock(0);
        setGameOver(true);
      }
    } else {
      setClock(newClock);
    }
  };

  const handleReset = () => {
    setGameStarted(false);
    setQuarter(1);
    setClock(SECONDS_PER_QUARTER);
    setScoreA(0);
    setScoreB(0);
    setGameOver(false);
    setTeamA('');
    setPlayersA([]);
    setTeamB('');
    setPlayersB([]);
  };

  return (
    <div style={{ padding: 32 }}>
      <h1>NBA Team vs Team Game</h1>
      {!gameStarted ? (
        <div style={{ display: 'flex', gap: 40 }}>
          <div>
            <h2>Team A</h2>
            <SelectTeam
              onSelectTeam={setTeamA}
              onSelectPlayers={setPlayersA}
              excludeTeam={teamB}
            />
            <div style={{ marginTop: 16 }}>
              <strong>Selected Team:</strong> {teamA || 'None'}
              <ul>
                {playersA.map((p) => (
                  <li key={p}>{p}</li>
                ))}
              </ul>
            </div>
          </div>
          <div>
            <h2>Team B</h2>
            <SelectTeam
              onSelectTeam={setTeamB}
              onSelectPlayers={setPlayersB}
              excludeTeam={teamA}
            />
            <div style={{ marginTop: 16 }}>
              <strong>Selected Team:</strong> {teamB || 'None'}
              <ul>
                {playersB.map((p) => (
                  <li key={p}>{p}</li>
                ))}
              </ul>
            </div>
          </div>
          <div style={{ alignSelf: 'flex-end' }}>
            <button
              onClick={handleStartGame}
              disabled={
                !teamA ||
                !teamB ||
                teamA === teamB ||
                playersA.length !== 5 ||
                playersB.length !== 5
              }
            >
              Start Game
            </button>
          </div>
        </div>
      ) : (
        <div>
          <h2>Game Started!</h2>
          <div style={{ display: 'flex', gap: 40 }}>
            <div>
              <h3>{teamA}</h3>
              <ul>
                {playersA.map((p) => (
                  <li key={p}>{p}</li>
                ))}
              </ul>
              <h3>Score: {scoreA}</h3>
            </div>
            <div>
              <h3>{teamB}</h3>
              <ul>
                {playersB.map((p) => (
                  <li key={p}>{p}</li>
                ))}
              </ul>
              <h3>Score: {scoreB}</h3>
            </div>
          </div>
          <div style={{ marginTop: 32 }}>
            <h3>
              Quarter: {quarter} / {QUARTERS}
            </h3>
            <h3>
              Time: {formatTime(clock)}
            </h3>
            {!gameOver ? (
              <div>Simulating...</div>
            ) : (
              <div>
                <h2>Game Over!</h2>
                <h2>
                  {scoreA === scoreB
                    ? 'It\'s a tie!'
                    : scoreA > scoreB
                    ? `${teamA} Wins!`
                    : `${teamB} Wins!`}
                </h2>
                <button onClick={handleReset}>Restart</button>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
