import React, { useState } from 'react';
import SelectTeam from './components/SelectTeam';

function App() {
  const [teamA, setTeamA] = useState('');
  const [playersA, setPlayersA] = useState([]);
  const [teamB, setTeamB] = useState('');
  const [playersB, setPlayersB] = useState([]);
  const [gameStarted, setGameStarted] = useState(false);

  const handleStartGame = () => {
    setGameStarted(true);
  };

  // Prevent selecting the same team for both sides
  const teamFilter = (exclude) => (team) => team !== exclude;

  return (
    <div style={{ padding: 32 }}>
      <h1>NBA Team vs Team Game</h1>
      {!gameStarted ? (
        <div style={{ display: 'flex', gap: 40 }}>
          <div>
            <h2>Team A</h2>
            <SelectTeam
              onSelectTeam={setTeamA}
              onSelectPlayers={setPlayersA}
              excludeTeam={teamB}
            />
            <div style={{ marginTop: 16 }}>
              <strong>Selected Team:</strong> {teamA || 'None'}
              <ul>
                {playersA.map((p) => (
                  <li key={p}>{p}</li>
                ))}
              </ul>
            </div>
          </div>
          <div>
            <h2>Team B</h2>
            <SelectTeam
              onSelectTeam={setTeamB}
              onSelectPlayers={setPlayersB}
              excludeTeam={teamA}
            />
            <div style={{ marginTop: 16 }}>
              <strong>Selected Team:</strong> {teamB || 'None'}
              <ul>
                {playersB.map((p) => (
                  <li key={p}>{p}</li>
                ))}
              </ul>
            </div>
          </div>
          <div style={{ alignSelf: 'flex-end' }}>
            <button
              onClick={handleStartGame}
              disabled={
                !teamA ||
                !teamB ||
                teamA === teamB ||
                playersA.length !== 5 ||
                playersB.length !== 5
              }
            >
              Start Game
            </button>
          </div>
        </div>
      ) : (
        <div>
          <h2>Game Started!</h2>
          <div style={{ display: 'flex', gap: 40 }}>
            <div>
              <h3>{teamA}</h3>
              <ul>
                {playersA.map((p) => (
                  <li key={p}>{p}</li>
                ))}
              </ul>
            </div>
            <div>
              <h3>{teamB}</h3>
              <ul>
                {playersB.map((p) => (
                  <li key={p}>{p}</li>
                ))}
              </ul>
            </div>
          </div>
          <h3 style={{ marginTop: 32 }}>Let the game begin!</h3>
        </div>
      )}
    </div>
  );
}

export default App;
